// require("dotenv").config();
require('dotenv').config({ path: './.env' });
const express = require("express");
const Razorpay = require('razorpay');
const bodyParser = require('body-parser');
const nodemailer = require("nodemailer");
const cors = require('cors');
const pdfMake = require('pdfmake/build/pdfmake');
const pdfFonts = require("pdfmake/build/vfs_fonts");


const app = express();
const port = 4242;

const razorpay = new Razorpay({
  key_id: process.env.RAZORPAY_KEY_ID,
  key_secret: process.env.RAZORPAY_SECRET,
});


app.use(cors());
app.use(bodyParser.json());

app.post('/create-order', async (req, res) => {
  const { amount } = req.body;

  const options = {
    amount: amount,
    currency: 'INR',
    receipt: `receipt#${Math.random().toString(36).substring(2, 15)}`,
    payment_capture: 1,
  };

  try {
    const order = await razorpay.orders.create(options);
    res.json(order);
  } catch (error) {
    res.status(500).json({ error: 'Internal server error' });
  }
});

app.post("/capture-payment/:paymentId", async (req, res) => {
  const { paymentId } = req.params;
  const { amount, email, name, category } = req.body;
  try {
    const response = await razorpay.payments.capture(paymentId, amount);

    if (response.error) {
      console.log("Error in capture payment")
      res.status(500).json({ error: response.error });
    } else {
      res.json(response);

      sendEmail(response, email, name, category);
    }
  } catch (error) {
    res.status(500).json({ error: error.message });
  }
});

async function sendEmail(response, email, name, category) {
  try {
    const transporter = nodemailer.createTransport({
      service: "gmail",
      auth: {
        user: process.env.EMAIL_USERNAME,
        pass: process.env.EMAIL_PASSWORD
      }
    });


    // Define the PDF document structure
    const docDefinition = {
      content: [
        // Add logo on top right corner
        {
          image: 'data:image/png;base64,',
          fit: [70, 70],
          absolutePosition: { x: 500, y: 15 },
          margin: [0, -10],
        },
        // Add background image
        {
          image: 'data:image/png;base64,',
          fit: [220, 220],
          absolutePosition: { x: 200, y: 290 },
          opacity: 0.4
        },

        // Add invoice details
        { text: 'OmniPass', style: 'header', alignment: 'center' },
        { text: 'All in One Ticket Booking Platform', style: 'subheader', alignment: 'center' },
        { text: '\nTicket cum Invoice', style: 'header', alignment: 'center' },
        { text: 'Customer Details:', style: 'subheader', margin: [0, 20, 0, 0] },
        { text: 'Name:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: name, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Email:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: email, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Contact:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: response.contact, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Booking Details:', style: 'subheader', margin: [0, 30, 0, 0] },
        { text: 'Ticket Category:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: category, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Ticket/Invoice Number:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: response.id, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Amount:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: `${response.currency} ${((response.amount) / 100).toFixed(2)}`, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Payment Method:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: response.method, style: 'content', margin: [300, 20, 0, 0] },
        { text: 'Booking Date:', style: 'subsubheader', margin: [40, 20, 0, -38] },
        { text: new Date().toString().substring(0, 24), style: 'content', margin: [300, 20, 0, 0] },
        { text: '*This is an Electronically Generated document and does not require Signature.', style: 'content', alignment: 'center', margin: [0, 100, 0, 0] },
        { text: new Date().toString().substring(0, 24), style: 'content', margin: [300, 20, 0, 0] },
      ],
      styles: {
        header: {
          fontSize: 24,
          bold: true,
          margin: [0, 0, 0, 20]
        },
        subheader: {
          fontSize: 20,
          bold: true,
          margin: [0, 0, 0, 5]
        },
        subsubheader: {
          fontSize: 17,
          bold: true,
          margin: [0, 0, 0, 5]
        },
        content: {
          fontSize: 15,
          bold: false,
          margin: [0, 0, 0, 5]
        }
      }
    };
    pdfMake.vfs = pdfFonts.pdfMake.vfs;
    const fileName = `Ticket-${response.id}.pdf`;

    const pdfDoc = pdfMake.createPdf(docDefinition);
    pdfDoc.getBuffer(async (buffer) => {
      // Send the email with the PDF file attached
      const info = await transporter.sendMail({
        from: '"OmniPass" <help.omnipass@gmail.com>',
        to: (response.email === email) ? email : [response.email, email],
        subject: 'Ticket cum Invoice',
        html: `
          <p>Dear ${name},</p>
          <p>Thank you for booking your ticket with <b>OmniPass</b>.</p>
          <p>Please find attached your ticket cum invoice with this email.</p>
          <p>If you have any questions or concerns, please do not hesitate to contact us.</p>
          <p>Thank you for choosing <b>OmniPass</b> for your ticket booking needs.</p>
          <p>Best regards,</p>
          <p><b>OmniPass Team</b></p>
        `,
        attachments: [{ filename: fileName, content: buffer }],
      });

      console.log('Message sent: %s', info.messageId);
    });
  } catch (error) {
    res.status(500).json({ error: 'Internal server error' });
  }
}



app.get('/check-payment-status/:paymentId', async (req, res) => {
  const { paymentId } = req.params;
  try {
    const payment = await razorpay.payments.fetch(paymentId);
    res.json(payment);
  } catch (error) {
    console.error(error);
    res.status(500).json({ error: 'Internal server error' });
  }
});

app.listen(port, () => {
  console.log(`Server running at http://localhost:${port}`);
});
